<?php require_once __DIR__.'/includes/config.php'; $error=''; $success=''; if($_SERVER['REQUEST_METHOD']==='POST'){ $name=trim($_POST['name']); $email=trim($_POST['email']); $password=trim($_POST['password']); if(!$name||!$email||!$password){ $error='All fields required.'; } else { $hash=password_hash($password,PASSWORD_BCRYPT); $stmt=$mysqli->prepare('INSERT INTO users (name,email,password_hash) VALUES (?,?,?)'); $stmt->bind_param('sss',$name,$email,$hash); if($stmt->execute()) $success='Account created'; else $error='Error: '.$mysqli->error; } } ?>
<!doctype html><html><head><meta charset="utf-8"><title>Register</title></head><body><h1>Register</h1><?php if($error) echo '<p style="color:red;">'.htmlspecialchars($error).'</p>'; if($success) echo '<p style="color:green;">'.htmlspecialchars($success).'</p>'; ?>
<form method="post"><label>Name:</label><br><input name="name" required><br><label>Email:</label><br><input type="email" name="email" required><br><label>Password:</label><br><input type="password" name="password" required><br><button type="submit">Register</button></form><p><a href="login.php">Back</a></p></body></html>